import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import AdminLayout from '../../components/AdminLayout';
import { 
  Plus, Edit, Trash2, Upload, Save, X, 
  BookOpen, Users, Clock, Award,
  Image as ImageIcon, FileText
} from 'lucide-react';
import toast from 'react-hot-toast';

const AITrainingAdmin = () => {
  const [trainingData, setTrainingData] = useState({
    title: 'AI培训',
    subtitle: '专业的人工智能培训课程',
    description: '从基础到高级，全面掌握AI技术',
    posterImages: [],
    courses: [],
    features: [],
    stats: {
      students: '1000+',
      courses: '50+',
      satisfaction: '98%',
      certificates: '800+'
    }
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [editingFeature, setEditingFeature] = useState(null);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchTrainingData();
  }, []);

  const fetchTrainingData = async () => {
    try {
      const response = await axios.get('/api/services/1'); // AI培训服务ID为1
      if (response.data) {
        setTrainingData(prev => ({
          ...prev,
          ...response.data
        }));
      }
    } catch (error) {
      console.error('获取AI培训数据失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    setUploading(true);
    const formData = new FormData();
    files.forEach(file => {
      formData.append('images', file);
    });

    try {
      const response = await axios.post('/api/upload/multiple', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setTrainingData(prev => ({
        ...prev,
        posterImages: [...prev.posterImages, ...response.data.filePaths]
      }));

      toast.success('图片上传成功！');
    } catch (error) {
      console.error('图片上传失败:', error);
      toast.error('图片上传失败');
    } finally {
      setUploading(false);
    }
  };

  const removeImage = (index) => {
    setTrainingData(prev => ({
      ...prev,
      posterImages: prev.posterImages.filter((_, i) => i !== index)
    }));
  };

  const saveCourse = (courseData) => {
    if (editingCourse !== null) {
      // 编辑现有课程
      setTrainingData(prev => ({
        ...prev,
        courses: prev.courses.map((course, index) => 
          index === editingCourse ? courseData : course
        )
      }));
    } else {
      // 添加新课程
      setTrainingData(prev => ({
        ...prev,
        courses: [...prev.courses, courseData]
      }));
    }
    setEditingCourse(null);
  };

  const deleteCourse = (index) => {
    if (window.confirm('确定要删除这个课程吗？')) {
      setTrainingData(prev => ({
        ...prev,
        courses: prev.courses.filter((_, i) => i !== index)
      }));
    }
  };

  const saveFeature = (featureData) => {
    if (editingFeature !== null) {
      // 编辑现有特色
      setTrainingData(prev => ({
        ...prev,
        features: prev.features.map((feature, index) => 
          index === editingFeature ? featureData : feature
        )
      }));
    } else {
      // 添加新特色
      setTrainingData(prev => ({
        ...prev,
        features: [...prev.features, featureData]
      }));
    }
    setEditingFeature(null);
  };

  const deleteFeature = (index) => {
    if (window.confirm('确定要删除这个特色吗？')) {
      setTrainingData(prev => ({
        ...prev,
        features: prev.features.filter((_, i) => i !== index)
      }));
    }
  };

  const saveAllData = async () => {
    try {
      await axios.put('/api/services/1', trainingData);
      toast.success('AI培训数据保存成功！');
      setIsEditing(false);
    } catch (error) {
      console.error('保存失败:', error);
      toast.error('保存失败');
    }
  };

  if (loading) {
    return (
      <AdminLayout>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>加载中...</p>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="admin-content">
        <div className="admin-header">
          <h1>AI培训管理</h1>
          <div className="admin-actions">
