import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import { 
  Calendar, User, Sparkles, Code, 
  Award, ChevronRight, Star, Globe, Smartphone,
  Monitor, Cpu, Database, Cloud, Mail, Phone, MessageSquare, Send
} from 'lucide-react';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [partners, setPartners] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPosts();
    fetchPartners();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('获取文章失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchPartners = async () => {
    try {
      const response = await axios.get('/api/partners');
      setPartners(response.data);
    } catch (error) {
      console.error('获取合作伙伴失败:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="xuelong-home">
      {/* Hero Section - 全屏主视觉 */}
      <motion.section 
        className="hero-fullscreen"
        style={{ paddingTop: '80px' }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.2 }}
      >
        <div className="hero-background">
          <div className="hero-overlay"></div>
          <div className="hero-particles"></div>
        </div>

        <div className="hero-content">
          <motion.div
            className="hero-text"
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.3 }}
          >
            <div className="logo-section">
              <img src="/logo.svg" alt="XUELONG AI" className="logo-icon" />
              <div className="title-container">
                <h1 className="main-title">XUELONG AI</h1>
                <span className="title-accent">雪珑人工智能设计工作室</span>
              </div>
            </div>
            <p className="hero-subtitle">
              引领智能工作新方式
            </p>
            <div className="hero-buttons">
              <motion.button 
                className="btn-hero primary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <ChevronRight size={20} />
                探索服务
              </motion.button>
              <motion.button 
                className="btn-hero secondary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                联系我们
              </motion.button>
            </div>
          </motion.div>
        </div>

        <div className="scroll-indicator">
          <motion.div 
            className="scroll-arrow"
            animate={{ y: [0, 10, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
          >
            <ChevronRight size={24} style={{ transform: 'rotate(90deg)' }} />
          </motion.div>
        </div>
      </motion.section>

      {/* 关于雪珑AI */}
      <section className="about-section">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">关于雪珑AI</h2>
            <p className="section-subtitle">
              专注于人工智能技术研发与应用的创新型工作室
            </p>
          </motion.div>

          <div className="about-content">
            <motion.div 
              className="about-text"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              <div className="about-card">
                <h3>技术创新</h3>
                <p>
                  雪珑AI工作室致力于推动人工智能技术的前沿发展，
                  专注于机器学习、深度学习、自然语言处理等核心技术领域。
                  我们拥有资深的技术团队和丰富的项目经验。
                </p>
              </div>
            </motion.div>

            <motion.div 
              className="about-stats"
              initial={{ opacity: 0, x: 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <div className="stats-grid">
                <div className="stat-item">
                  <div className="stat-number">50+</div>
                  <div className="stat-label">成功项目</div>
                </div>
                <div className="stat-item">
                  <div className="stat-number">100+</div>
                  <div className="stat-label">培训学员</div>
                </div>
                <div className="stat-item">
                  <div className="stat-number">5+</div>
                  <div className="stat-label">年经验</div>
                </div>
                <div className="stat-item">
                  <div className="stat-number">20+</div>
                  <div className="stat-label">合作伙伴</div>
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* 核心服务展示 */}
      <section className="services-overview-section">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">核心服务</h2>
            <p className="section-subtitle">
              全方位AI服务生态，从培训到开发，从合作到资讯
            </p>
          </motion.div>

          <motion.div
            className="services-overview-content"
            initial={{ opacity: 0, scale: 0.9 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <div className="services-image-container">
              <img 
                src="/services-overview.svg" 
                alt="雪珑AI核心服务" 
                className="services-overview-image"
              />
            </div>

            <div className="services-description">
              <div className="service-highlights">
                <motion.div 
                  className="highlight-item"
                  initial={{ opacity: 0, x: -30 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.6, delay: 0.3 }}
                  viewport={{ once: true }}
                >
                  <div className="highlight-icon">
                    <Code size={24} />
                  </div>
                  <div className="highlight-content">
                    <h4>专业AI培训</h4>
                    <p>从基础到进阶，全面的人工智能课程体系</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="highlight-item"
                  initial={{ opacity: 0, x: -30 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                  viewport={{ once: true }}
                >
                  <div className="highlight-icon">
                    <Monitor size={24} />
                  </div>
                  <div className="highlight-content">
                    <h4>定制项目开发</h4>
                    <p>量身打造的AI解决方案，满足您的业务需求</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="highlight-item"
                  initial={{ opacity: 0, x: -30 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.6, delay: 0.5 }}
                  viewport={{ once: true }}
                >
                  <div className="highlight-icon">
                    <Award size={24} />
                  </div>
                  <div className="highlight-content">
                    <h4>行业合作伙伴</h4>
                    <p>与领先企业携手，共创AI技术新未来</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="highlight-item"
                  initial={{ opacity: 0, x: -30 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.6, delay: 0.6 }}
                  viewport={{ once: true }}
                >
                  <div className="highlight-icon">
                    <Sparkles size={24} />
                  </div>
                  <div className="highlight-content">
                    <h4>前沿技术资讯</h4>
                    <p>第一时间了解AI领域的最新进展和突破</p>
                  </div>
                </motion.div>
              </div>

              <motion.div 
                className="services-cta"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.7 }}
                viewport={{ once: true }}
              >
                <button className="btn-services-primary">
                  探索全部服务
                  <ChevronRight size={20} />
                </button>
                <button className="btn-services-secondary">
                  联系我们
                </button>
              </motion.div>
            </div>
          </motion.div>
        </div>
      </section>

       {/* 联系我们 */}
       <section className="contact-section">
         <div className="container">
           <motion.div
             className="contact-content"
             initial={{ opacity: 0, y: 50 }}
             whileInView={{ opacity: 1, y: 0 }}
             transition={{ duration: 0.8 }}
             viewport={{ once: true }}
           >
             <div className="contact-text">
               <h2>开启AI合作之旅</h2>
               <p>
                 无论您是想学习AI技术，还是需要定制化的AI解决方案，
                 雪珑AI工作室都将是您最佳的合作伙伴。
               </p>
               <div className="contact-info">
                 <div className="contact-item">
                   <strong>邮箱：</strong>contact@xuelongai.com
                 </div>
                 <div className="contact-item">
                   <strong>电话：</strong>400-888-9999
                 </div>
                 <div className="contact-item">
                   <strong>地址：</strong>北京市海淀区中关村科技园
                 </div>
               </div>
             </div>
             <div className="contact-form">
               <h3>立即咨询</h3>
               <form>
                 <div className="form-row">
                   <input type="text" placeholder="您的姓名" />
                   <input type="email" placeholder="邮箱地址" />
                 </div>
                 <input type="tel" placeholder="联系电话" />
                 <select>
                   <option>请选择服务类型</option>
                   <option>AI培训</option>
                   <option>项目开发</option>
                   <option>技术咨询</option>
                   <option>合作洽谈</option>
                 </select>
                 <textarea placeholder="请描述您的需求..."></textarea>
                 <motion.button 
                   type="submit" 
                   className="btn-submit"
                   whileHover={{ scale: 1.05 }}
                   whileTap={{ scale: 0.95 }}
                 >
                   提交咨询
                 </motion.button>
               </form>
             </div>
           </motion.div>
         </div>
       </section>
     </div>
   );
 };

 export default Home;
