import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import AdminLayout from '../../components/AdminLayout';
import { 
  Plus, Edit, Trash2, Upload, Save, X, 
  Code, Cpu, Database, Cloud,
  Image as ImageIcon, FileText
} from 'lucide-react';
import toast from 'react-hot-toast';

const ProjectDevelopmentAdmin = () => {
  const [projectData, setProjectData] = useState({
    title: '项目开发',
    subtitle: '定制化AI解决方案',
    description: '专业的项目开发服务，满足您的业务需求',
    posterImages: [],
    services: [],
    caseStudies: [],
    stats: {
      projects: '200+',
      satisfaction: '98%',
      clients: '100+',
      support: '24/7'
    }
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editingService, setEditingService] = useState(null);
  const [editingCase, setEditingCase] = useState(null);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchProjectData();
  }, []);

  const fetchProjectData = async () => {
    try {
      const response = await axios.get('/api/services/2'); // 项目开发服务ID为2
      if (response.data) {
        setProjectData(prev => ({
          ...prev,
          ...response.data
        }));
      }
    } catch (error) {
      console.error('获取项目开发数据失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    setUploading(true);
    const formData = new FormData();
    files.forEach(file => {
      formData.append('images', file);
    });

    try {
      const response = await axios.post('/api/upload/multiple', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setProjectData(prev => ({
        ...prev,
        posterImages: [...prev.posterImages, ...response.data.filePaths]
      }));

      toast.success('图片上传成功！');
    } catch (error) {
      console.error('图片上传失败:', error);
      toast.error('图片上传失败');
    } finally {
      setUploading(false);
    }
  };

  const removeImage = (index) => {
    setProjectData(prev => ({
      ...prev,
      posterImages: prev.posterImages.filter((_, i) => i !== index)
    }));
  };

  const saveService = (serviceData) => {
    if (editingService !== null) {
      // 编辑现有服务
      setProjectData(prev => ({
        ...prev,
        services: prev.services.map((service, index) => 
          index === editingService ? serviceData : service
        )
      }));
    } else {
      // 添加新服务
      setProjectData(prev => ({
        ...prev,
        services: [...prev.services, serviceData]
      }));
    }
    setEditingService(null);
  };

  const deleteService = (index) => {
    if (window.confirm('确定要删除这个服务吗？')) {
      setProjectData(prev => ({
        ...prev,
        services: prev.services.filter((_, i) => i !== index)
      }));
    }
  };

  const saveCase = (caseData) => {
    if (editingCase !== null) {
      // 编辑现有案例
      setProjectData(prev => ({
        ...prev,
        caseStudies: prev.caseStudies.map((caseStudy, index) => 
          index === editingCase ? caseData : caseStudy
        )
      }));
    } else {
      // 添加新案例
      setProjectData(prev => ({
        ...prev,
        caseStudies: [...prev.caseStudies, caseData]
      }));
    }
    setEditingCase(null);
  };

  const deleteCase = (index) => {
    if (window.confirm('确定要删除这个案例吗？')) {
      setProjectData(prev => ({
        ...prev,
        caseStudies: prev.caseStudies.filter((_, i) => i !== index)
      }));
    }
  };

  const saveAllData = async () => {
    try {
      await axios.put('/api/services/2', projectData);
      toast.success('项目开发数据保存成功！');
      setIsEditing(false);
    } catch (error) {
      console.error('保存失败:', error);
      toast.error('保存失败');
    }
  };

  if (loading) {
    return (
      <AdminLayout>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>加载中...</p>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="admin-content">
        <div className="admin-header">
          <h1>项目开发管理</h1>
          <div className="admin-actions">
            {isEditing ? (
              <>
                <button className="btn-save" onClick={saveAllData}>
                  <Save size={20} />
                  保存所有更改
                </button>
                <button className="btn-cancel" onClick={() => setIsEditing(false)}>
                  <X size={20} />
                  取消
                </button>
              </>
            ) : (
              <button className="btn-edit" onClick={() => setIsEditing(true)}>
                <Edit size={20} />
                编辑内容
              </button>
            )}
          </div>
        </div>

        {/* 基本信息编辑 */}
        <motion.div 
          className="admin-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h2>基本信息</h2>
          <div className="form-grid">
            <div className="form-group">
              <label>标题</label>
              <input
                type="text"
                value={projectData.title}
                onChange={(e) => setProjectData(prev => ({ ...prev, title: e.target.value }))}
                disabled={!isEditing}
                className="form-input"
              />
            </div>
            <div className="form-group">
              <label>副标题</label>
              <input
                type="text"
                value={projectData.subtitle}
                onChange={(e) => setProjectData(prev => ({ ...prev, subtitle: e.target.value }))}
                disabled={!isEditing}
                className="form-input"
              />
            </div>
            <div className="form-group full-width">
              <label>描述</label>
              <textarea
                value={projectData.description}
                onChange={(e) => setProjectData(prev => ({ ...prev, description: e.target.value }))}
                disabled={!isEditing}
                className="form-textarea"
                rows={3}
              />
            </div>
          </div>
        </motion.div>

        {/* 统计数据编辑 */}
        <motion.div 
          className="admin-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.1 }}
        >
          <h2>统计数据</h2>
          <div className="stats-grid">
            <div className="form-group">
              <label>完成项目</label>
              <input
                type="text"
                value={projectData.stats.projects}
                onChange={(e) => setProjectData(prev => ({ 
                  ...prev, 
                  stats: { ...prev.stats, projects: e.target.value }
                }))}
                disabled={!isEditing}
                className="form-input"
              />
            </div>
            <div className="form-group">
              <label>客户满意度</label>
              <input
                type="text"
                value={projectData.stats.satisfaction}
                onChange={(e) => setProjectData(prev => ({ 
                  ...prev, 
                  stats: { ...prev.stats, satisfaction: e.target.value }
                }))}
                disabled={!isEditing}
                className="form-input"
              />
            </div>
            <div className="form-group">
              <label>合作企业</label>
              <input
                type="text"
                value={projectData.stats.clients}
                onChange={(e) => setProjectData(prev => ({ 
                  ...prev, 
                  stats: { ...prev.stats, clients: e.target.value }
                }))}
                disabled={!isEditing}
                className="form-input"
              />
            </div>
            <div className="form-group">
              <label>技术支持</label>
              <input
                type="text"
                value={projectData.stats.support}
                onChange={(e) => setProjectData(prev => ({ 
                  ...prev, 
                  stats: { ...prev.stats, support: e.target.value }
                }))}
                disabled={!isEditing}
                className="form-input"
              />
            </div>
          </div>
        </motion.div>

        {/* 海报图片管理 */}
        <motion.div 
          className="admin-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          <h2>海报图片</h2>
          {isEditing && (
            <div className="upload-section">
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: 'none' }}
                id="image-upload"
              />
              <label htmlFor="image-upload" className="btn-upload">
                <Upload size={20} />
                {uploading ? '上传中...' : '上传图片'}
              </label>
            </div>
          )}

          <div className="images-grid">
            {projectData.posterImages.map((image, index) => (
              <div key={index} className="image-item">
                <img 
                  src={`http://localhost:5000${image}`} 
                  alt={`海报 ${index + 1}`}
                  className="admin-image"
                />
                {isEditing && (
                  <button 
                    className="btn-remove-image"
                    onClick={() => removeImage(index)}
                  >
                    <X size={16} />
                  </button>
                )}
              </div>
            ))}
          </div>
        </motion.div>

        {/* 服务管理 */}
        <motion.div 
          className="admin-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.3 }}
        >
          <div className="section-header">
            <h2>服务管理</h2>
            {isEditing && (
              <button 
                className="btn-add"
                onClick={() => setEditingService('new')}
              >
                <Plus size={20} />
                添加服务
              </button>
            )}
          </div>

          <div className="services-list">
            {projectData.services.map((service, index) => (
              <div key={index} className="service-item">
                <div className="service-info">
                  <h3>{service.title}</h3>
                  <p>{service.description}</p>
                  <div className="service-meta">
                    <span className="price">{service.price}</span>
                    <span className="duration">{service.duration}</span>
                    <span className={`complexity complexity-${service.complexity}`}>
                      {service.complexity}复杂度
                    </span>
                  </div>
                </div>
                {isEditing && (
                  <div className="service-actions">
                    <button 
                      className="btn-edit-small"
                      onClick={() => setEditingService(index)}
                    >
                      <Edit size={16} />
                    </button>
                    <button 
                      className="btn-delete"
                      onClick={() => deleteService(index)}
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </motion.div>

        {/* 案例管理 */}
        <motion.div 
          className="admin-section"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.4 }}
        >
          <div className="section-header">
            <h2>案例管理</h2>
            {isEditing && (
              <button 
                className="btn-add"
                onClick={() => setEditingCase('new')}
              >
                <Plus size={20} />
                添加案例
              </button>
            )}
          </div>

          <div className="cases-list">
            {projectData.caseStudies.map((caseStudy, index) => (
              <div key={index} className="case-item">
                <div className="case-info">
                  <h3>{caseStudy.title}</h3>
                  <p className="case-client">{caseStudy.client}</p>
                  <p>{caseStudy.description}</p>
                  <div className="case-result">
                    <strong>项目成果：</strong>
                    <span>{caseStudy.result}</span>
                  </div>
                </div>
                {isEditing && (
                  <div className="case-actions">
                    <button 
                      className="btn-edit-small"
                      onClick={() => setEditingCase(index)}
                    >
                      <Edit size={16} />
                    </button>
                    <button 
                      className="btn-delete"
                      onClick={() => deleteCase(index)}
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </motion.div>
      </div>

      {/* 服务编辑模态框 */}
      {editingService !== null && (
        <ServiceEditModal
          service={editingService === 'new' ? null : projectData.services[editingService]}
          onSave={saveService}
          onClose={() => setEditingService(null)}
        />
      )}

      {/* 案例编辑模态框 */}
      {editingCase !== null && (
        <CaseEditModal
          caseStudy={editingCase === 'new' ? null : projectData.caseStudies[editingCase]}
          onSave={saveCase}
          onClose={() => setEditingCase(null)}
        />
      )}
    </AdminLayout>
  );
};

// 服务编辑模态框组件
const ServiceEditModal = ({ service, onSave, onClose }) => {
  const [formData, setFormData] = useState({
    title: service?.title || '',
    description: service?.description || '',
    price: service?.price || '',
    duration: service?.duration || '',
    complexity: service?.complexity || '中',
    features: service?.features || []
  });

  const [newFeature, setNewFeature] = useState('');

  const addFeature = () => {
    if (newFeature.trim()) {
      setFormData(prev => ({
        ...prev,
        features: [...prev.features, newFeature.trim()]
      }));
      setNewFeature('');
    }
  };

  const removeFeature = (index) => {
    setFormData(prev => ({
      ...prev,
      features: prev.features.filter((_, i) => i !== index)
    }));
  };

  const handleSave = () => {
    if (!formData.title || !formData.description) {
      toast.error('请填写必要信息');
      return;
    }
    onSave(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>{service ? '编辑服务' : '添加服务'}</h3>
          <button className="btn-close" onClick={onClose}>
            <X size={20} />
          </button>
        </div>

        <div className="modal-body">
          <div className="form-group">
            <label>服务标题</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              className="form-input"
            />
          </div>

          <div className="form-group">
            <label>服务描述</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="form-textarea"
              rows={3}
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>价格</label>
              <input
                type="text"
                value={formData.price}
                onChange={(e) => setFormData(prev => ({ ...prev, price: e.target.value }))}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label>时长</label>
              <input
                type="text"
                value={formData.duration}
                onChange={(e) => setFormData(prev => ({ ...prev, duration: e.target.value }))}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label>复杂度</label>
              <select
                value={formData.complexity}
                onChange={(e) => setFormData(prev => ({ ...prev, complexity: e.target.value }))}
                className="form-select"
              >
                <option value="中">中</option>
                <option value="中高">中高</option>
                <option value="高">高</option>
              </select>
            </div>
          </div>

          <div className="form-group">
            <label>服务特色</label>
            <div className="features-input">
              <input
                type="text"
                value={newFeature}
                onChange={(e) => setNewFeature(e.target.value)}
                placeholder="添加服务特色"
                className="form-input"
                onKeyPress={(e) => e.key === 'Enter' && addFeature()}
              />
              <button type="button" onClick={addFeature} className="btn-add-feature">
                <Plus size={16} />
              </button>
            </div>

            <div className="features-list">
              {formData.features.map((feature, index) => (
                <div key={index} className="feature-tag">
                  <span>{feature}</span>
                  <button 
                    type="button"
                    onClick={() => removeFeature(index)}
                    className="btn-remove-feature"
                  >
                    <X size={14} />
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="modal-footer">
          <button className="btn-cancel" onClick={onClose}>取消</button>
          <button className="btn-save" onClick={handleSave}>保存</button>
        </div>
      </div>
    </div>
  );
};

// 案例编辑模态框组件
const CaseEditModal = ({ caseStudy, onSave, onClose }) => {
  const [formData, setFormData] = useState({
    title: caseStudy?.title || '',
    client: caseStudy?.client || '',
    description: caseStudy?.description || '',
    result: caseStudy?.result || '',
    technologies: caseStudy?.technologies || [],
    image: caseStudy?.image || ''
  });

  const [newTechnology, setNewTechnology] = useState('');

  const addTechnology = () => {
    if (newTechnology.trim()) {
      setFormData(prev => ({
        ...prev,
        technologies: [...prev.technologies, newTechnology.trim()]
      }));
      setNewTechnology('');
    }
  };

  const removeTechnology = (index) => {
    setFormData(prev => ({
      ...prev,
      technologies: prev.technologies.filter((_, i) => i !== index)
    }));
  };

  const handleSave = () => {
    if (!formData.title || !formData.client || !formData.description) {
      toast.error('请填写必要信息');
      return;
    }
    onSave(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content large">
        <div className="modal-header">
          <h3>{caseStudy ? '编辑案例' : '添加案例'}</h3>
          <button className="btn-close" onClick={onClose}>
            <X size={20} />
          </button>
        </div>

        <div className="modal-body">
          <div className="form-row">
            <div className="form-group">
              <label>案例标题</label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label>客户名称</label>
              <input
                type="text"
                value={formData.client}
                onChange={(e) => setFormData(prev => ({ ...prev, client: e.target.value }))}
                className="form-input"
              />
            </div>
          </div>

          <div className="form-group">
            <label>案例描述</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="form-textarea"
              rows={3}
            />
          </div>

          <div className="form-group">
            <label>项目成果</label>
            <textarea
              value={formData.result}
              onChange={(e) => setFormData(prev => ({ ...prev, result: e.target.value }))}
              className="form-textarea"
              rows={2}
            />
          </div>

          <div className="form-group">
            <label>案例图片URL</label>
            <input
              type="text"
              value={formData.image}
              onChange={(e) => setFormData(prev => ({ ...prev, image: e.target.value }))}
              className="form-input"
              placeholder="/uploads/case-example.jpg"
            />
          </div>

          <div className="form-group">
            <label>使用技术</label>
            <div className="features-input">
              <input
                type="text"
                value={newTechnology}
                onChange={(e) => setNewTechnology(e.target.value)}
                placeholder="添加技术栈"
                className="form-input"
                onKeyPress={(e) => e.key === 'Enter' && addTechnology()}
              />
              <button type="button" onClick={addTechnology} className="btn-add-feature">
                <Plus size={16} />
              </button>
            </div>

            <div className="features-list">
              {formData.technologies.map((tech, index) => (
                <div key={index} className="feature-tag">
                  <span>{tech}</span>
                  <button 
                    type="button"
                    onClick={() => removeTechnology(index)}
                    className="btn-remove-feature"
                  >
                    <X size={14} />
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="modal-footer">
          <button className="btn-cancel" onClick={onClose}>取消</button>
          <button className="btn-save" onClick={handleSave}>保存</button>
        </div>
      </div>
    </div>
  );
};

export default ProjectDevelopmentAdmin;
