import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Calendar, User, ArrowRight, Sparkles, Brain, Zap } from 'lucide-react';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('获取文章失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div>
      {/* Hero Section */}
      <motion.section 
        className="hero"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="container">
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <h1>
              <Sparkles className="inline-block mr-4" size={48} />
              AI Studio
            </h1>
            <p>探索人工智能的无限可能，创造未来科技</p>
            <motion.div
              className="flex justify-center gap-4 mt-8"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              <motion.button 
                className="btn btn-primary" 
                style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                onClick={() => navigate('/login')}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Brain size={20} />
                开始探索
                <ArrowRight size={16} />
              </motion.button>
              <motion.button 
                className="btn btn-secondary" 
                style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                onClick={() => {
                  const featuresSection = document.querySelector('section:nth-of-type(2)');
                  featuresSection?.scrollIntoView({ behavior: 'smooth' });
                }}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Zap size={20} />
                了解更多
              </motion.button>
            </motion.div>
          </motion.div>
        </div>
      </motion.section>

      {/* Features Section */}
      <section style={{ padding: '80px 0', background: 'rgba(255, 255, 255, 0.1)' }}>
        <div className="container">
          <motion.div
            className="text-center mb-16"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 style={{ fontSize: '3rem', fontWeight: '700', color: 'white', marginBottom: '16px' }}>
              我们的优势
            </h2>
            <p style={{ fontSize: '1.2rem', color: 'rgba(255, 255, 255, 0.8)' }}>
              领先的AI技术，专业的团队，创新的解决方案
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                icon: <Brain size={48} />,
                title: '智能算法',
                description: '采用最先进的机器学习和深度学习算法，提供精准的AI解决方案'
              },
              {
                icon: <Zap size={48} />,
                title: '高效处理',
                description: '优化的计算架构，确保快速响应和高效的数据处理能力'
              },
              {
                icon: <Sparkles size={48} />,
                title: '创新体验',
                description: '用户友好的界面设计，让AI技术变得简单易用，人人可及'
              }
            ].map((feature, index) => (
              <motion.div
                key={index}
                className="card text-center"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -10 }}
              >
                <div style={{ color: '#667eea', marginBottom: '16px' }}>
                  {feature.icon}
                </div>
                <h3 style={{ fontSize: '1.5rem', fontWeight: '600', marginBottom: '12px' }}>
                  {feature.title}
                </h3>
                <p style={{ color: '#666', lineHeight: '1.6' }}>
                  {feature.description}
                </p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Posts Section */}
      <section style={{ padding: '80px 0' }}>
        <div className="container">
          <motion.div
            className="text-center mb-16"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 style={{ fontSize: '3rem', fontWeight: '700', color: 'white', marginBottom: '16px' }}>
              最新动态
            </h2>
            <p style={{ fontSize: '1.2rem', color: 'rgba(255, 255, 255, 0.8)' }}>
              了解AI领域的最新进展和我们的研究成果
            </p>
          </motion.div>

          {loading ? (
            <div className="text-center" style={{ color: 'white', fontSize: '18px' }}>
              加载中...
            </div>
          ) : (
            <div className="posts-grid">
              {posts.map((post, index) => (
                <motion.div
                  key={post.id}
                  className="post-card"
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                  whileHover={{ y: -10 }}
                >
                  {post.image && (
                    <img 
                      src={post.image} 
                      alt={post.title}
                      className="post-image"
                    />
                  )}
                  <div className="post-content">
                    <h3 className="post-title">{post.title}</h3>
                    <p className="post-text">{post.content}</p>
                    <div className="post-meta">
                      <span style={{ display: 'flex', alignItems: 'center', gap: '4px' }}>
                        <User size={14} />
                        {post.author}
                      </span>
                      <span style={{ display: 'flex', alignItems: 'center', gap: '4px' }}>
                        <Calendar size={14} />
                        {formatDate(post.createdAt)}
                      </span>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {posts.length === 0 && !loading && (
            <div className="text-center" style={{ color: 'white', fontSize: '18px' }}>
              暂无文章
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Home;
