import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import { 
  Calendar, User, Sparkles, Brain, Code, 
  Award, ChevronRight, Star, Globe, Smartphone,
  Monitor, Cpu, Database, Cloud, Mail, Phone, MessageSquare, Send
} from 'lucide-react';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [partners, setPartners] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPosts();
    fetchPartners();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('获取文章失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchPartners = async () => {
    try {
      const response = await axios.get('/api/partners');
      setPartners(response.data);
    } catch (error) {
      console.error('获取合作伙伴失败:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="xuelong-home">
      {/* Hero Section - 全屏主视觉 */}
      <motion.section 
        className="hero-fullscreen"
        style={{ paddingTop: '80px' }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.2 }}
      >
        <div className="hero-background">
          <div className="hero-overlay"></div>
          <div className="hero-particles"></div>
        </div>

        <div className="hero-content">
          <motion.div
            className="hero-text"
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.3 }}
          >
            <div className="logo-section">
              <img src="/logo.svg" alt="XUELONG AI Logo" className="logo-icon" />
              <h1 className="main-title">
              XUELONG AI <span className="title-accent">雪珑人工智能设计工作室</span>
