import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { X, Code, Monitor, Award, ChevronLeft } from 'lucide-react';
import axios from 'axios';

const ServiceDetail = ({ serviceId, onClose }) => {
  const [service, setService] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchService = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/services/${serviceId}`);
        setService(response.data);
      } catch (err) {
        setError('获取服务详情失败');
        console.error('Error fetching service:', err);
      } finally {
        setLoading(false);
      }
    };

    if (serviceId) {
      fetchService();
    }
  }, [serviceId]);

  const getIcon = (iconName) => {
    switch (iconName) {
      case 'Code':
        return <Code size={48} />;
      case 'Monitor':
        return <Monitor size={48} />;
      case 'Award':
        return <Award size={48} />;
      default:
        return <Code size={48} />;
    }
  };

  if (loading) {
    return (
      <motion.div 
        className="service-detail-overlay"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <div className="service-detail-modal">
          <div className="loading-spinner">加载中...</div>
        </div>
      </motion.div>
    );
  }

  if (error) {
    return (
      <motion.div 
        className="service-detail-overlay"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <div className="service-detail-modal">
          <div className="error-message">{error}</div>
          <button onClick={onClose} className="btn-primary">关闭</button>
        </div>
      </motion.div>
    );
  }

  if (!service) {
    return null;
  }

  return (
    <motion.div 
      className="service-detail-overlay"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      onClick={onClose}
    >
      <motion.div 
        className="service-detail-modal"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        transition={{ duration: 0.3 }}
        onClick={(e) => e.stopPropagation()}
      >
        <div className="service-detail-header">
          <button 
            className="close-button"
            onClick={onClose}
            aria-label="关闭"
          >
            <X size={24} />
          </button>
        </div>

        <div className="service-detail-content">
          <div className="service-detail-icon">
            {getIcon(service.icon)}
          </div>

          <h2 className="service-detail-title">{service.title}</h2>

          <p className="service-detail-description">{service.description}</p>

          {service.features && service.features.length > 0 && (
            <div className="service-features-section">
              <h3>核心特性</h3>
              <div className="service-features-grid">
                {service.features.map((feature, index) => (
                  <motion.div 
                    key={index}
                    className="feature-card"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <span className="feature-text">{feature}</span>
                  </motion.div>
                ))}
              </div>
            </div>
          )}

          <div className="service-detail-actions">
            <motion.button 
              className="btn-primary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                // 这里可以添加联系我们或咨询的逻辑
                alert('联系我们获取更多信息');
              }}
            >
              立即咨询
            </motion.button>

            <motion.button 
              className="btn-secondary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={onClose}
            >
              <ChevronLeft size={20} />
              返回
            </motion.button>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default ServiceDetail;
