import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';

// 组件导入
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import AITraining from './pages/AITraining';
import ProjectDevelopment from './pages/ProjectDevelopment';
import Admin from './components/Admin';
import AdminDashboard from './pages/admin/Dashboard';
import AdminPosts from './pages/admin/Posts';
import CreatePost from './pages/admin/CreatePost';
import EditPost from './pages/admin/EditPost';
import AITrainingAdmin from './pages/admin/AITrainingAdmin';
import ProjectDevelopmentAdmin from './pages/admin/ProjectDevelopmentAdmin';

// 使用相对路径，通过package.json中的proxy配置
// axios.defaults.baseURL = 'http://localhost:5000';

// 认证上下文
export const AuthContext = React.createContext();

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      const userData = localStorage.getItem('user');
      if (userData) {
        setUser(JSON.parse(userData));
      }
    }
    setLoading(false);
  }, []);

  const login = (token, userData) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    setUser(userData);
    toast.success('登录成功！');
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    delete axios.defaults.headers.common['Authorization'];
    setUser(null);
    toast.success('已退出登录');
  };

  const ProtectedRoute = ({ children }) => {
    if (loading) {
      return (
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          color: 'white',
          fontSize: '18px'
        }}>
          XUELONG AI
        </div>
      );
    }

    return user ? children : <Navigate to="/login" />;
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        color: 'white',
        fontSize: '18px'
      }}>
        XUELONG AI
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/ai-training" element={<AITraining />} />
          <Route path="/project-development" element={<ProjectDevelopment />} />
          <Route 
            path="/login" 
            element={user ? <Navigate to="/admin" /> : <Login />} 
          />
          <Route 
            path="/admin" 
            element={
              <ProtectedRoute>
                <Admin />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/dashboard" 
            element={
              <ProtectedRoute>
                <AdminDashboard />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/posts" 
            element={
              <ProtectedRoute>
                <AdminPosts />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/posts/create" 
            element={
              <ProtectedRoute>
                <CreatePost />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/posts/edit/:id" 
            element={
              <ProtectedRoute>
                <EditPost />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/ai-training" 
            element={
              <ProtectedRoute>
                <AITrainingAdmin />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/project-development" 
            element={
              <ProtectedRoute>
                <ProjectDevelopmentAdmin />
              </ProtectedRoute>
            } 
          />
          {/* 404 路由 - 处理未匹配的路径 */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </AuthContext.Provider>
  );
}

export default App;
