import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('company');
  const [companyInfo, setCompanyInfo] = useState({});
  const [founderInfo, setFounderInfo] = useState({});
  const [companyIntro, setCompanyIntro] = useState({});
  const [aiResources, setAiResources] = useState([]);
  const [services, setServices] = useState([]);
  const [posts, setPosts] = useState([]);
  const [partners, setPartners] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      const [companyRes, founderRes, introRes, aiRes, servicesRes, postsRes, partnersRes, statsRes] = await Promise.all([
        axios.get('/api/company'),
        axios.get('/api/founder'),
        axios.get('/api/company-intro'),
        axios.get('/api/ai-resources'),
        axios.get('/api/services'),
        axios.get('/api/posts'),
        axios.get('/api/partners'),
        axios.get('/api/dashboard/stats')
      ]);

      setCompanyInfo(companyRes.data);
      setFounderInfo(founderRes.data);
      setCompanyIntro(introRes.data);
      setAiResources(aiRes.data);
      setServices(servicesRes.data);
      setPosts(postsRes.data);
      setPartners(partnersRes.data);
      setStats(statsRes.data);
    } catch (error) {
      console.error('Error loading data:', error);
      setMessage('加载数据失败');
    } finally {
      setLoading(false);
    }
  };

  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => setMessage(''), 3000);
  };

  const updateCompanyInfo = async (data) => {
    try {
      await axios.put('/api/company', data);
      setCompanyInfo(data);
      showMessage('公司信息更新成功');
    } catch (error) {
      showMessage('更新失败');
    }
  };

  const updateFounderInfo = async (data) => {
    try {
      await axios.put('/api/founder', data);
      setFounderInfo(data);
      showMessage('创始人信息更新成功');
    } catch (error) {
      showMessage('更新失败');
    }
  };

  const updateCompanyIntro = async (data) => {
    try {
      await axios.put('/api/company-intro', data);
      setCompanyIntro(data);
      showMessage('公司介绍更新成功');
    } catch (error) {
      showMessage('更新失败');
    }
  };

  const addAiResource = async (resource) => {
    try {
      const response = await axios.post('/api/ai-resources', resource);
      setAiResources([...aiResources, response.data]);
      showMessage('AI资源添加成功');
    } catch (error) {
      showMessage('添加失败');
    }
  };

  const updateAiResource = async (id, resource) => {
    try {
      const response = await axios.put(`/api/ai-resources/${id}`, resource);
      setAiResources(aiResources.map(r => r.id === id ? response.data : r));
      showMessage('AI资源更新成功');
    } catch (error) {
      showMessage('更新失败');
    }
  };

  const deleteAiResource = async (id) => {
    try {
      await axios.delete(`/api/ai-resources/${id}`);
      setAiResources(aiResources.filter(r => r.id !== id));
      showMessage('AI资源删除成功');
    } catch (error) {
      showMessage('删除失败');
    }
  };

  const addService = async (service) => {
    try {
      const response = await axios.post('/api/services', service);
      setServices([...services, response.data]);
      showMessage('服务添加成功');
    } catch (error) {
      showMessage('添加失败');
    }
  };

  const updateService = async (id, service) => {
    try {
      const response = await axios.put(`/api/services/${id}`, service);
      setServices(services.map(s => s.id === id ? response.data : s));
      showMessage('服务更新成功');
    } catch (error) {
      showMessage('更新失败');
    }
  };

  const deleteService = async (id) => {
    try {
      await axios.delete(`/api/services/${id}`);
      setServices(services.filter(s => s.id !== id));
      showMessage('服务删除成功');
    } catch (error) {
      showMessage('删除失败');
    }
  };

  const renderCompanyTab = () => (
    <div className="admin-section">
      <h3>公司信息管理</h3>
      <CompanyForm 
        data={companyInfo} 
        onSubmit={updateCompanyInfo}
      />
    </div>
  );

  const renderFounderTab = () => (
    <div className="admin-section">
      <h3>创始人信息管理</h3>
      <FounderForm 
        data={founderInfo} 
        onSubmit={updateFounderInfo}
      />
    </div>
  );

  const renderIntroTab = () => (
    <div className="admin-section">
      <h3>公司介绍管理</h3>
      <IntroForm 
        data={companyIntro} 
        onSubmit={updateCompanyIntro}
      />
    </div>
  );

  const renderAiResourcesTab = () => (
    <div className="admin-section">
      <h3>AI资源管理</h3>
      <AiResourcesManager 
        resources={aiResources}
        onAdd={addAiResource}
        onUpdate={updateAiResource}
        onDelete={deleteAiResource}
      />
    </div>
  );

  const renderServicesTab = () => (
    <div className="admin-section">
      <h3>核心服务管理</h3>
      <ServicesManager 
        services={services}
        onAdd={addService}
        onUpdate={updateService}
        onDelete={deleteService}
      />
    </div>
  );

  const renderDashboardTab = () => (
    <div className="admin-section">
      <h3>数据统计</h3>
      <div className="stats-grid">
        <div className="stat-card">
          <h4>文章总数</h4>
          <p>{stats.totalPosts}</p>
        </div>
        <div className="stat-card">
          <h4>合作伙伴</h4>
          <p>{stats.totalPartners}</p>
        </div>
        <div className="stat-card">
          <h4>AI资源</h4>
          <p>{stats.totalAiResources}</p>
        </div>
        <div className="stat-card">
          <h4>核心服务</h4>
          <p>{stats.totalServices}</p>
        </div>
        <div className="stat-card">
          <h4>活跃AI资源</h4>
          <p>{stats.activeAiResources}</p>
        </div>
        <div className="stat-card">
          <h4>活跃服务</h4>
          <p>{stats.activeServices}</p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h1>雪龙AI后台管理系统</h1>
        {message && <div className="message">{message}</div>}
      </div>

      <div className="admin-tabs">
        <button 
          className={activeTab === 'dashboard' ? 'active' : ''}
          onClick={() => setActiveTab('dashboard')}
        >
          数据统计
        </button>
        <button 
          className={activeTab === 'company' ? 'active' : ''}
          onClick={() => setActiveTab('company')}
        >
          公司信息
        </button>
        <button 
          className={activeTab === 'founder' ? 'active' : ''}
          onClick={() => setActiveTab('founder')}
        >
          创始人信息
        </button>
        <button 
          className={activeTab === 'intro' ? 'active' : ''}
          onClick={() => setActiveTab('intro')}
        >
          公司介绍
        </button>
        <button 
          className={activeTab === 'ai-resources' ? 'active' : ''}
          onClick={() => setActiveTab('ai-resources')}
        >
          AI资源
        </button>
        <button 
          className={activeTab === 'services' ? 'active' : ''}
          onClick={() => setActiveTab('services')}
        >
          核心服务
        </button>
      </div>

      <div className="admin-content">
        {loading ? (
          <div className="loading">加载中...</div>
        ) : (
          <>
            {activeTab === 'dashboard' && renderDashboardTab()}
            {activeTab === 'company' && renderCompanyTab()}
            {activeTab === 'founder' && renderFounderTab()}
            {activeTab === 'intro' && renderIntroTab()}
            {activeTab === 'ai-resources' && renderAiResourcesTab()}
            {activeTab === 'services' && renderServicesTab()}
          </>
        )}
      </div>
    </div>
  );
};

// Company Form Component
const CompanyForm = ({ data, onSubmit }) => {
  const [formData, setFormData] = useState(data);

  useEffect(() => {
    setFormData(data);
  }, [data]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="admin-form">
      <div className="form-group">
        <label>公司名称</label>
        <input
          type="text"
          value={formData.name || ''}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>副标题</label>
        <input
          type="text"
          value={formData.subtitle || ''}
          onChange={(e) => setFormData({...formData, subtitle: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>标语</label>
        <input
          type="text"
          value={formData.slogan || ''}
          onChange={(e) => setFormData({...formData, slogan: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>公司描述</label>
        <textarea
          value={formData.description || ''}
          onChange={(e) => setFormData({...formData, description: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>地址</label>
        <input
          type="text"
          value={formData.address || ''}
          onChange={(e) => setFormData({...formData, address: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>电话</label>
        <input
          type="text"
          value={formData.phone || ''}
          onChange={(e) => setFormData({...formData, phone: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>邮箱</label>
        <input
          type="email"
          value={formData.email || ''}
          onChange={(e) => setFormData({...formData, email: e.target.value})}
        />
      </div>
      <button type="submit" className="btn-primary">更新公司信息</button>
    </form>
  );
};

// Founder Form Component
const FounderForm = ({ data, onSubmit }) => {
  const [formData, setFormData] = useState(data);
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);

  useEffect(() => {
    setFormData(data);
    setPhotoPreview(data.photo);
  }, [data]);

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPhotoFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setPhotoPreview(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (photoFile) {
      // 如果有新上传的文件，使用FormData
      const formDataToSend = new FormData();
      formDataToSend.append('name', formData.name || '');
      formDataToSend.append('title', formData.title || '');
      formDataToSend.append('description', formData.description || '');
      formDataToSend.append('photo', photoFile);

      try {
        await axios.put('/api/founder', formDataToSend, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        // 重新加载数据
        window.location.reload();
      } catch (error) {
        console.error('更新失败:', error);
      }
    } else {
      // 没有新文件，使用原来的方式
      onSubmit(formData);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="admin-form">
      <div className="form-group">
        <label>姓名</label>
        <input
          type="text"
          value={formData.name || ''}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>职位</label>
        <input
          type="text"
          value={formData.title || ''}
          onChange={(e) => setFormData({...formData, title: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>描述</label>
        <textarea
          value={formData.description || ''}
          onChange={(e) => setFormData({...formData, description: e.target.value})}
        />
      </div>
      <div className="form-group">
        <label>照片</label>
        {photoPreview && (
          <div style={{ marginBottom: '10px' }}>
            <img 
              src={photoPreview.startsWith('/uploads') ? `http://localhost:5000${photoPreview}` : photoPreview} 
              alt="创始人照片预览" 
              style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '8px' }}
            />
          </div>
        )}
        <input
          type="file"
          accept="image/*"
          onChange={handlePhotoChange}
          style={{ marginBottom: '10px' }}
        />
        <div style={{ fontSize: '12px', color: '#666' }}>
          支持 JPG、PNG、GIF 格式，建议尺寸 200x200 像素
        </div>
      </div>
      <button type="submit" className="btn-primary">更新创始人信息</button>
    </form>
  );
};

// Company Intro Form Component
const IntroForm = ({ data, onSubmit }) => {
  const [formData, setFormData] = useState(data);

  useEffect(() => {
    setFormData(data);
  }, [data]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const updateParagraph = (index, value) => {
    const newParagraphs = [...(formData.paragraphs || [])];
    newParagraphs[index] = value;
    setFormData({...formData, paragraphs: newParagraphs});
  };

  const addParagraph = () => {
    const newParagraphs = [...(formData.paragraphs || []), ''];
    setFormData({...formData, paragraphs: newParagraphs});
  };

  const removeParagraph = (index) => {
    const newParagraphs = formData.paragraphs.filter((_, i) => i !== index);
    setFormData({...formData, paragraphs: newParagraphs});
  };

  return (
    <form onSubmit={handleSubmit} className="admin-form">
      <div className="form-group">
        <label>公司介绍段落</label>
        {(formData.paragraphs || []).map((paragraph, index) => (
          <div key={index} className="paragraph-input">
            <textarea
              value={paragraph}
              onChange={(e) => updateParagraph(index, e.target.value)}
              placeholder={`段落 ${index + 1}`}
            />
            <button 
              type="button" 
              onClick={() => removeParagraph(index)}
              className="btn-danger"
            >
              删除
            </button>
          </div>
        ))}
        <button type="button" onClick={addParagraph} className="btn-secondary">
          添加段落
        </button>
      </div>
      <button type="submit" className="btn-primary">更新公司介绍</button>
    </form>
  );
};

// AI Resources Manager Component
const AiResourcesManager = ({ resources, onAdd, onUpdate, onDelete }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingResource, setEditingResource] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    url: '',
    isActive: true
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingResource) {
      onUpdate(editingResource.id, formData);
      setEditingResource(null);
    } else {
      onAdd(formData);
    }
    setFormData({ name: '', description: '', category: '', url: '', isActive: true });
    setShowForm(false);
  };

  const handleEdit = (resource) => {
    setEditingResource(resource);
    setFormData(resource);
    setShowForm(true);
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingResource(null);
    setFormData({ name: '', description: '', category: '', url: '', isActive: true });
  };

  return (
    <div>
      <div className="section-header">
        <button 
          onClick={() => setShowForm(true)} 
          className="btn-primary"
        >
          添加AI资源
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="admin-form">
          <div className="form-group">
            <label>名称</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              required
            />
          </div>
          <div className="form-group">
            <label>描述</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              required
            />
          </div>
          <div className="form-group">
            <label>分类</label>
            <input
              type="text"
              value={formData.category}
              onChange={(e) => setFormData({...formData, category: e.target.value})}
            />
          </div>
          <div className="form-group">
            <label>链接</label>
            <input
              type="url"
              value={formData.url}
              onChange={(e) => setFormData({...formData, url: e.target.value})}
            />
          </div>
          <div className="form-group">
            <label>
              <input
                type="checkbox"
                checked={formData.isActive}
                onChange={(e) => setFormData({...formData, isActive: e.target.checked})}
              />
              激活状态
            </label>
          </div>
          <div className="form-actions">
            <button type="submit" className="btn-primary">
              {editingResource ? '更新' : '添加'}
            </button>
            <button type="button" onClick={handleCancel} className="btn-secondary">
              取消
            </button>
          </div>
        </form>
      )}

      <div className="resources-list">
        {resources.map(resource => (
          <div key={resource.id} className="resource-item">
            <div className="resource-info">
              <h4>{resource.name}</h4>
              <p>{resource.description}</p>
              <span className={`status ${resource.isActive ? 'active' : 'inactive'}`}>
                {resource.isActive ? '激活' : '未激活'}
              </span>
            </div>
            <div className="resource-actions">
              <button onClick={() => handleEdit(resource)} className="btn-secondary">
                编辑
              </button>
              <button onClick={() => onDelete(resource.id)} className="btn-danger">
                删除
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Services Manager Component
const ServicesManager = ({ services, onAdd, onUpdate, onDelete }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingService, setEditingService] = useState(null);

  const [formData, setFormData] = useState({
    title: '',
    templateType: 'vertical'
  });
  const [posterFiles, setPosterFiles] = useState([]);
  const [posterPreviews, setPosterPreviews] = useState([]);
  const [existingImages, setExistingImages] = useState([]); // 存储现有的服务器图片
  const [newImages, setNewImages] = useState([]); // 存储新添加的图片预览

  const handlePosterChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 0) {
      // 对于横版模板，支持增量添加图片
      if (formData.templateType === 'horizontal' && editingService) {
        // 编辑模式下，将新文件添加到现有文件列表
        setPosterFiles(prevFiles => [...prevFiles, ...files]);

        const newPreviews = [];
        files.forEach(file => {
          const reader = new FileReader();
          reader.onload = (e) => {
            newPreviews.push({
              url: e.target.result,
              isNew: true,
              fileIndex: posterFiles.length + newPreviews.length
            });
            if (newPreviews.length === files.length) {
              setNewImages(prevNew => [...prevNew, ...newPreviews]);
              // 更新总预览列表
              setPosterPreviews(prevPreviews => [...prevPreviews, ...newPreviews.map(p => p.url)]);
            }
          };
          reader.readAsDataURL(file);
        });
      } else {
        // 其他情况保持原有逻辑（替换所有图片）
        setPosterFiles(files);
        setExistingImages([]);
        setNewImages([]);
        const previews = [];
        files.forEach(file => {
          const reader = new FileReader();
          reader.onload = (e) => {
            previews.push(e.target.result);
            if (previews.length === files.length) {
              setPosterPreviews(previews);
            }
          };
          reader.readAsDataURL(file);
        });
      }
    }
    // 清空文件输入框，允许重复选择相同文件
    e.target.value = '';
  };

  const handleRemoveImage = (indexToRemove) => {
    const existingCount = existingImages.length;

    if (indexToRemove < existingCount) {
      // 删除现有图片
      setExistingImages(prevExisting => 
        prevExisting.filter((_, index) => index !== indexToRemove)
      );
    } else {
      // 删除新添加的图片
      const newImageIndex = indexToRemove - existingCount;
      setNewImages(prevNew => 
        prevNew.filter((_, index) => index !== newImageIndex)
      );
      setPosterFiles(prevFiles => 
        prevFiles.filter((_, index) => index !== newImageIndex)
      );
    }

    // 更新总预览列表
    setPosterPreviews(prevPreviews => 
      prevPreviews.filter((_, index) => index !== indexToRemove)
    );
  };

  const handleClearAllImages = () => {
    if (window.confirm('确定要清空所有图片吗？此操作不可撤销。')) {
      setPosterPreviews([]);
      setPosterFiles([]);
      setExistingImages([]);
      setNewImages([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (posterFiles.length === 0 && !editingService) {
      alert('请选择海报图片');
      return;
    }

    const serviceData = {
      title: formData.title,
      templateType: formData.templateType,
      isActive: true,
      order: 1
    };

    if (editingService) {
      // 编辑现有服务 - 只更新海报
      if (posterFiles.length > 0) {
        try {
          const posterFormData = new FormData();
          posterFiles.forEach(file => {
            posterFormData.append('posters', file);
          });

          // 对于横版模板，如果有现有图片，使用追加模式
          const isAppendMode = formData.templateType === 'horizontal' && 
                              editingService.posterImages && 
                              editingService.posterImages.length > 0;

          const url = isAppendMode 
            ? `/api/services/${editingService.id}/poster?append=true`
            : `/api/services/${editingService.id}/poster`;

          const response = await axios.post(url, posterFormData, {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          });

          serviceData.posterImage = response.data.posterUrl;
          onUpdate(editingService.id, serviceData);
          alert(isAppendMode ? '图片添加成功！' : '海报更新成功！');
        } catch (error) {
          console.error('海报上传失败:', error);
          alert('海报上传失败，请重试');
          return;
        }
      }
      setEditingService(null);
    } else {
      // 新增服务
      const newService = await onAdd(serviceData);

      if (posterFiles.length > 0 && newService && newService.id) {
        try {
          const posterFormData = new FormData();
          posterFiles.forEach(file => {
            posterFormData.append('posters', file);
          });

          await axios.post(`/api/services/${newService.id}/poster`, posterFormData, {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          });
          alert('服务添加成功！');
        } catch (error) {
          console.error('海报上传失败:', error);
          alert('服务创建成功，但海报上传失败，请重新编辑上传海报');
        }
      }
    }

    // 重置表单
     setFormData({ title: '', templateType: 'vertical' });
    setPosterFiles([]);
    setPosterPreviews([]);
    setShowForm(false);
  };

  const handleEdit = (service) => {
    setEditingService(service);
    setFormData({
      title: service.title,
      templateType: service.templateType || 'vertical'
    });

    // 清空所有状态
    setPosterFiles([]);
    setNewImages([]);

    // 根据模板类型设置预览和现有图片
    if ((service.templateType === 'grid' || service.templateType === 'horizontal') && service.posterImages) {
      const imageUrls = service.posterImages.map(img => img.startsWith('http') ? img : `/api${img}`);
      setPosterPreviews(imageUrls);
      setExistingImages(imageUrls);
    } else if (service.posterImage) {
      const imageUrl = service.posterImage.startsWith('http') ? service.posterImage : `/api${service.posterImage}`;
      setPosterPreviews([imageUrl]);
      setExistingImages([imageUrl]);
    } else {
      setPosterPreviews([]);
      setExistingImages([]);
    }

    setShowForm(true);
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingService(null);
    setFormData({ title: '', templateType: 'vertical' });
    setPosterFiles([]);
    setPosterPreviews([]);
  };

  // 已移除特性管理相关函数，简化为只管理海报

  return (
    <div>
      <div className="section-header">
        <button 
          onClick={() => setShowForm(true)} 
          className="btn-primary"
        >
          添加服务
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="admin-form">
          <div className="form-group">
            <label>标题</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              required
            />
          </div>
          <div className="form-group">
            <label>模板类型</label>
            <select
              value={formData.templateType}
              onChange={(e) => setFormData({...formData, templateType: e.target.value})}
            >
              <option value="vertical">竖版单张海报</option>
              <option value="horizontal">横版多图海报</option>
              <option value="grid">九宫格多图</option>
            </select>
          </div>
          <div className="form-group">
            <label>
              {formData.templateType === 'vertical' && '竖版海报图片 (建议比例9:16)'}
              {formData.templateType === 'horizontal' && '横版海报图片 (可选择多张，建议比例16:9)'}
              {formData.templateType === 'grid' && '九宫格图片 (可选择多张，建议正方形比例1:1)'}
            </label>
            <input
              type="file"
              accept="image/jpeg,image/jpg,image/png,image/webp,image/gif,image/bmp"
              onChange={handlePosterChange}
              className="file-input"
              multiple={formData.templateType === 'grid' || formData.templateType === 'horizontal'}
            />
            {formData.templateType === 'horizontal' && editingService && posterPreviews.length > 0 && (
              <div style={{ marginTop: '10px' }}>
                <div style={{ fontSize: '12px', color: '#666', marginBottom: '8px' }}>
                  提示：选择新图片将添加到现有图片中，而不是替换
                </div>
                <button
                  type="button"
                  onClick={handleClearAllImages}
                  style={{
                    background: '#dc3545',
                    color: 'white',
                    border: 'none',
                    padding: '6px 12px',
                    borderRadius: '4px',
                    fontSize: '12px',
                    cursor: 'pointer'
                  }}
                >
                  清空所有图片
                </button>
              </div>
            )}
            {posterPreviews.length > 0 && (
              <div className="image-preview">
                {posterPreviews.map((preview, index) => (
                  <div key={index} style={{ position: 'relative', display: 'inline-block', margin: '5px' }}>
                    <img 
                      src={preview} 
                      alt={`海报预览 ${index + 1}`} 
                      style={{
                        maxWidth: '150px', 
                        maxHeight: formData.templateType === 'horizontal' ? '100px' : '200px',
                        objectFit: 'cover',
                        aspectRatio: formData.templateType === 'vertical' ? '9/16' : formData.templateType === 'horizontal' ? '16/9' : '1/1'
                      }} 
                    />
                    {(formData.templateType === 'horizontal' || formData.templateType === 'grid') && (
                      <button
                        type="button"
                        onClick={() => handleRemoveImage(index)}
                        style={{
                          position: 'absolute',
                          top: '5px',
                          right: '5px',
                          background: 'rgba(220, 53, 69, 0.8)',
                          color: 'white',
                          border: 'none',
                          borderRadius: '50%',
                          width: '20px',
                          height: '20px',
                          fontSize: '12px',
                          cursor: 'pointer',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center'
                        }}
                        title="删除图片"
                      >
                        ×
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="form-actions">
            <button type="submit" className="btn-primary">
              {editingService ? '更新海报' : '添加服务'}
            </button>
            <button type="button" onClick={handleCancel} className="btn-secondary">
              取消
            </button>
          </div>
        </form>
      )}

      <div className="services-list">
        {services.map(service => (
          <div key={service.id} className="service-item">
            <div className="service-info">
               <h4>{service.title}</h4>
               <p>模板类型: {service.templateType === 'vertical' ? '竖版单张' : service.templateType === 'horizontal' ? '横版多图' : '九宫格多图'}</p>
               <div className="service-poster-preview">
                 {(service.templateType === 'grid' || service.templateType === 'horizontal') && service.posterImages && service.posterImages.length > 0 ? (
                   service.posterImages.map((image, index) => (
                     <img 
                       key={index}
                       src={image.startsWith('http') ? image : `/api${image}`}
                       alt={`${service.title} ${index + 1}`}
                       style={{
                         width: service.templateType === 'horizontal' ? '80px' : '60px', 
                         height: service.templateType === 'horizontal' ? '45px' : '60px', 
                         objectFit: 'cover', 
                         margin: '2px'
                       }}
                     />
                   ))
                 ) : service.posterImage ? (
                   <img 
                     src={service.posterImage.startsWith('http') ? service.posterImage : `/api${service.posterImage}`}
                     alt={service.title}
                     style={{
                       width: service.templateType === 'horizontal' ? '120px' : '80px', 
                       height: service.templateType === 'horizontal' ? '70px' : '120px', 
                       objectFit: 'cover'
                     }}
                   />
                 ) : (
                   <div style={{width: '80px', height: '120px', backgroundColor: '#f0f0f0', display: 'flex', alignItems: 'center', justifyContent: 'center'}}>无图片</div>
                 )}
               </div>
             </div>
            <div className="service-actions">
              <button onClick={() => handleEdit(service)} className="btn-secondary">
                更换海报
              </button>
              <button onClick={() => onDelete(service.id)} className="btn-danger">
                删除
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Admin;
