import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import '../styles/AITraining.css';
import { 
  BookOpen, Users, Award, Clock, 
  CheckCircle, Star, ArrowRight, Play,
  Code, Brain, Target, Zap
} from 'lucide-react';

const AITraining = () => {
  const [trainingData, setTrainingData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTrainingData();
  }, []);

  const fetchTrainingData = async () => {
    try {
      const response = await axios.get('/api/services/1'); // AI培训服务ID为1
      setTrainingData(response.data);
    } catch (error) {
      console.error('获取培训数据失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const courses = [
    {
      id: 1,
      title: 'AI基础理论课程',
      description: '从零开始学习人工智能的基础概念、数学原理和核心算法',
      duration: '8周',
      level: '初级',
      students: 1200,
      rating: 4.8,
      features: ['机器学习基础', '深度学习入门', '数据预处理', '模型评估'],
      price: '¥2,999',
      image: '/uploads/course-basic.jpg'
    },
    {
      id: 2,
      title: '深度学习实战',
      description: '掌握TensorFlow、PyTorch等主流框架，完成实际项目开发',
      duration: '12周',
      level: '中级',
      students: 800,
      rating: 4.9,
      features: ['神经网络设计', '计算机视觉', '自然语言处理', '项目实战'],
      price: '¥4,999',
      image: '/uploads/course-advanced.jpg'
    },
    {
      id: 3,
      title: 'AI工程师认证',
      description: '企业级AI项目开发，获得行业认可的专业认证',
      duration: '16周',
      level: '高级',
      students: 500,
      rating: 4.9,
      features: ['MLOps实践', '模型部署', '性能优化', '企业项目'],
      price: '¥8,999',
      image: '/uploads/course-expert.jpg'
    }
  ];

  const features = [
    {
      icon: <Users size={32} />,
      title: '专家导师团队',
      description: '来自顶级科技公司的AI专家亲自授课'
    },
    {
      icon: <Code size={32} />,
      title: '实战项目驱动',
      description: '通过真实项目学习，积累实际开发经验'
    },
    {
      icon: <Award size={32} />,
      title: '权威认证证书',
      description: '完成课程获得行业认可的专业认证'
    },
    {
      icon: <Target size={32} />,
      title: '就业推荐服务',
      description: '优秀学员推荐至合作企业就业'
    }
  ];

  const stats = [
    { number: '5000+', label: '培训学员' },
    { number: '95%', label: '就业率' },
    { number: '50+', label: '合作企业' },
    { number: '4.9', label: '课程评分' }
  ];

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>加载中...</p>
      </div>
    );
  }

  return (
    <div className="ai-training-page">
      {/* Hero Section */}
      <section className="training-hero">
        <div className="container">
          <div className="hero-content">
            <motion.div
              className="hero-text"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h1>专业AI培训</h1>
              <p className="hero-subtitle">
                从基础到进阶，全面的人工智能课程体系
              </p>
              <p className="hero-description">
                由行业专家精心设计的AI培训课程，结合理论学习与实战项目，
                帮助您快速掌握人工智能核心技术，成为AI领域的专业人才。
              </p>
              <div className="hero-buttons">
                <button className="btn-primary">
                  <Play size={20} />
                  开始学习
                </button>
                <button className="btn-secondary">
                  查看课程
                  <ArrowRight size={20} />
                </button>
              </div>
            </motion.div>

            <motion.div
              className="hero-image"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              {trainingData?.posterImages && trainingData.posterImages.length > 0 && (
                <img 
                  src={`http://localhost:5000${trainingData.posterImages[0]}`} 
                  alt="AI培训" 
                  className="hero-main-image"
                />
              )}
            </motion.div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="training-stats">
        <div className="container">
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                className="stat-item"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="stat-number">{stat.number}</div>
                <div className="stat-label">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="training-features">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>为什么选择我们的AI培训</h2>
            <p>专业、实用、高效的AI学习体验</p>
          </motion.div>

          <div className="features-grid">
            {features.map((feature, index) => (
              <motion.div
                key={index}
                className="feature-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="feature-icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Courses Section */}
      <section className="training-courses">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>精品课程</h2>
            <p>系统化的AI学习路径，从入门到精通</p>
          </motion.div>

          <div className="courses-grid">
            {courses.map((course, index) => (
              <motion.div
                key={course.id}
                className="course-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="course-image">
                  <img src={course.image} alt={course.title} />
                  <div className="course-level">{course.level}</div>
                </div>

                <div className="course-content">
                  <h3>{course.title}</h3>
                  <p className="course-description">{course.description}</p>

                  <div className="course-meta">
                    <div className="course-info">
                      <span><Clock size={16} /> {course.duration}</span>
                      <span><Users size={16} /> {course.students}人学习</span>
                    </div>
                    <div className="course-rating">
                      <Star size={16} fill="currentColor" />
                      <span>{course.rating}</span>
                    </div>
                  </div>

                  <div className="course-features">
                    {course.features.map((feature, idx) => (
                      <span key={idx} className="feature-tag">
                        <CheckCircle size={14} />
                        {feature}
                      </span>
                    ))}
                  </div>

                  <div className="course-footer">
                    <div className="course-price">{course.price}</div>
                    <button className="btn-course">
                      立即报名
                      <ArrowRight size={16} />
                    </button>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="training-cta">
        <div className="container">
          <motion.div
            className="cta-content"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>开启您的AI学习之旅</h2>
            <p>加入我们，成为下一个AI专家</p>
            <div className="cta-buttons">
              <button className="btn-primary">
                <Brain size={20} />
                免费试听
              </button>
              <button className="btn-secondary">
                咨询课程
                <ArrowRight size={20} />
              </button>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default AITraining;
