import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Mail, Phone, MapPin, Send, MessageCircle, Clock } from 'lucide-react';
import toast from 'react-hot-toast';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // 这里可以添加实际的表单提交逻辑
      await new Promise(resolve => setTimeout(resolve, 1000)); // 模拟API调用
      toast.success('消息发送成功！我们会尽快回复您。');
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: '',
        message: ''
      });
    } catch (error) {
      toast.error('发送失败，请稍后重试。');
    } finally {
      setIsSubmitting(false);
    }
  };

  const contactInfo = [
    {
      icon: Mail,
      title: '邮箱地址',
      content: 'contact@xuelongai.com',
      description: '我们会在24小时内回复您的邮件'
    },
    {
      icon: Phone,
      title: '联系电话',
      content: '+86 138-0000-0000',
      description: '工作日 9:00-18:00'
    },
    {
      icon: MapPin,
      title: '办公地址',
      content: '北京市朝阳区科技园区',
      description: '欢迎预约到访参观'
    },
    {
      icon: Clock,
      title: '工作时间',
      content: '周一至周五 9:00-18:00',
      description: '节假日可通过邮件联系'
    }
  ];

  return (
    <div className="xuelong-home">
      {/* Hero Section */}
      <section className="hero-section contact-hero">
        <div className="container">
          <motion.div 
            className="hero-content"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="hero-title">
              联系我们
              <span className="title-accent">Contact Us</span>
            </h1>
            <p className="hero-subtitle">
              有任何问题或合作意向，欢迎随时与我们联系
            </p>
          </motion.div>
        </div>
      </section>

      {/* Contact Info Section */}
      <section className="contact-info-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">联系方式</h2>
            <p className="section-subtitle">多种方式与我们取得联系</p>
          </motion.div>

          <div className="contact-info-grid">
            {contactInfo.map((info, index) => (
              <motion.div
                key={index}
                className="contact-info-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -5 }}
              >
                <div className="contact-info-icon">
                  <info.icon size={24} />
                </div>
                <h3>{info.title}</h3>
                <p className="contact-info-content">{info.content}</p>
                <p className="contact-info-description">{info.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact Form Section */}
      <section className="contact-form-section">
        <div className="container">
          <div className="contact-form-wrapper">
            <motion.div 
              className="contact-form-content"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              <h2>发送消息</h2>
              <p>填写下方表单，我们会尽快与您取得联系</p>

              <form onSubmit={handleSubmit} className="contact-form">
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="name">姓名 *</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                      placeholder="请输入您的姓名"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="email">邮箱 *</label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      placeholder="请输入您的邮箱"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="phone">电话</label>
                    <input
                      type="tel"
                      id="phone"
                      name="phone"
                      value={formData.phone}
                      onChange={handleChange}
                      placeholder="请输入您的电话号码"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="subject">主题 *</label>
                    <input
                      type="text"
                      id="subject"
                      name="subject"
                      value={formData.subject}
                      onChange={handleChange}
                      required
                      placeholder="请输入消息主题"
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="message">消息内容 *</label>
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    rows="6"
                    placeholder="请详细描述您的需求或问题..."
                  ></textarea>
                </div>

                <motion.button
                  type="submit"
                  className="btn-submit"
                  disabled={isSubmitting}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  {isSubmitting ? (
                    <>
                      <div className="spinner"></div>
                      发送中...
                    </>
                  ) : (
                    <>
                      <Send size={18} />
                      发送消息
                    </>
                  )}
                </motion.button>
              </form>
            </motion.div>

            <motion.div 
              className="contact-form-image"
              initial={{ opacity: 0, x: 50 }}
