import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import ServiceDetail from '../components/ServiceDetail';
import { 
  Calendar, User, Sparkles, Code, 
  Award, ChevronRight, Star, Globe, Smartphone,
  Monitor, Cpu, Database, Cloud, Mail, Phone, MessageSquare, Send
} from 'lucide-react';

// 动态图片轮播组件
const ServiceImageCarousel = ({ services }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [allImages, setAllImages] = useState([]);

  useEffect(() => {
    // 收集所有服务的图片
    const images = [];
    services.forEach(service => {
      if (service.posterImages && service.posterImages.length > 0) {
        service.posterImages.forEach(image => {
          images.push({
            url: image,
            serviceTitle: service.title
          });
        });
      }
    });
    setAllImages(images);
  }, [services]);

  useEffect(() => {
    if (allImages.length > 1) {
      const interval = setInterval(() => {
        setCurrentImageIndex(prev => (prev + 1) % allImages.length);
      }, 3000); // 每3秒切换一张图片
      return () => clearInterval(interval);
    }
  }, [allImages.length]);

  if (allImages.length === 0) {
    return (
      <img 
        src="/services-overview.svg" 
        alt="雪珑AI核心服务" 
        className="services-overview-image"
      />
    );
  }

  return (
    <motion.div 
      className="services-carousel-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <motion.img
        key={currentImageIndex}
        src={allImages[currentImageIndex].url.startsWith('/api/') ? 
          allImages[currentImageIndex].url : 
          `/api${allImages[currentImageIndex].url}`}
        alt={`${allImages[currentImageIndex].serviceTitle} - 服务展示`}
        className="services-overview-image carousel-image"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.8 }}
      />
      {allImages.length > 1 && (
        <div className="carousel-indicators">
          {allImages.map((_, index) => (
            <button
              key={index}
              className={`carousel-indicator ${index === currentImageIndex ? 'active' : ''}`}
              onClick={() => setCurrentImageIndex(index)}
              aria-label={`切换到第${index + 1}张图片`}
            />
          ))}
        </div>
      )}
    </motion.div>
  );
};

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [partners, setPartners] = useState([]);
  const [companyInfo, setCompanyInfo] = useState({});
  const [founderInfo, setFounderInfo] = useState({});
  const [companyIntro, setCompanyIntro] = useState({});
  const [aiResources, setAiResources] = useState([]);
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedServiceId, setSelectedServiceId] = useState(null);
  const [showServiceDetail, setShowServiceDetail] = useState(false);

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      const [postsRes, partnersRes, companyRes, founderRes, introRes, aiRes, servicesRes] = await Promise.all([
        axios.get('/api/posts'),
        axios.get('/api/partners'),
        axios.get('/api/company'),
        axios.get('/api/founder'),
        axios.get('/api/company-intro'),
        axios.get('/api/ai-resources'),
        axios.get('/api/services')
      ]);

      setPosts(postsRes.data);
      setPartners(partnersRes.data);
      setCompanyInfo(companyRes.data);
      setFounderInfo(founderRes.data);
      setCompanyIntro(introRes.data);
      setAiResources(aiRes.data.filter(resource => resource.isActive));
      setServices(servicesRes.data.filter(service => service.isActive));
    } catch (error) {
      console.error('获取数据失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleServiceDetail = (serviceId) => {
    setSelectedServiceId(serviceId);
    setShowServiceDetail(true);
  };

  const closeServiceDetail = () => {
    setShowServiceDetail(false);
    setSelectedServiceId(null);
  };

  return (
    <div className="xuelong-home">
      {/* Hero Section - 全屏主视觉 */}
      <motion.section 
        className="hero-fullscreen"
        style={{ paddingTop: '80px' }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.2 }}
      >
        <div className="hero-background">
          <div className="hero-overlay"></div>
          <div className="hero-particles"></div>
        </div>

        <div className="hero-content">
          <motion.div
            className="hero-text"
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.3 }}
          >
            <div className="logo-section">
              <img src="/logo.svg" alt="XUELONG AI" className="logo-icon" />
              <div className="title-container">
                <h1 className="main-title">{companyInfo.name || 'XUELONG AI'}</h1>
                <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                  <span className="title-accent">{companyInfo.subtitle || '雪珑人工智能设计工作室'}</span>
                  <motion.button 
                    className="btn-hero secondary"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    style={{ 
                      fontSize: '1rem', 
                      padding: '8px 16px',
                      height: 'auto',
                      lineHeight: '1.2',
                      display: 'flex',
                      alignItems: 'center'
                    }}
                  >
                    引领智能工作新方式
                  </motion.button>
                </div>
                <p className="hero-subtitle" style={{ textAlign: 'center', margin: '8px 0 0 0' }}>专注于人工智能技术研发与应用的创新型工作室</p>
              </div>
            </div>
          </motion.div>
        </div>

        <div className="scroll-indicator">
          <motion.div 
            className="scroll-arrow"
            animate={{ y: [0, 10, 0] }}
            transition={{ duration: 2, repeat: Infinity }}
          >
            <ChevronRight size={24} style={{ transform: 'rotate(90deg)' }} />
          </motion.div>
        </div>
      </motion.section>

      {/* 关于雪珑AI */}
      <section className="about-section">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2 className="section-title">关于雪珑AI</h2>
            <p className="section-subtitle">
              专注于人工智能技术研发与应用的创新型工作室
            </p>
          </motion.div>

          <div className="about-content">
            <div className="about-main-row">
              <motion.div 
                className="about-text"
                initial={{ opacity: 0, x: -50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.8 }}
                viewport={{ once: true }}
              >
                <div className="about-card">
                  <h3>技术创新</h3>
                  <div className="innovation-content">
                    <div className="founder-section">
                      <div className="founder-photo">
