import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import { 
  Code, Cpu, Database, Cloud, 
  CheckCircle, Star, ArrowRight, Play,
  Zap, Shield, Globe, Smartphone,
  Monitor, Settings, Users, Award
} from 'lucide-react';

const ProjectDevelopment = () => {
  const [projectData, setProjectData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProjectData();
  }, []);

  const fetchProjectData = async () => {
    try {
      const response = await axios.get('/api/services/2'); // 项目开发服务ID为2
      setProjectData(response.data);
    } catch (error) {
      console.error('获取项目数据失败:', error);
    } finally {
      setLoading(false);
    }
  };

  const services = [
    {
      id: 1,
      title: 'AI算法开发',
      description: '定制化机器学习和深度学习算法，解决特定业务问题',
      icon: <Cpu size={40} />,
      features: ['自然语言处理', '计算机视觉', '推荐系统', '预测分析'],
      price: '¥50,000起',
      duration: '2-6个月',
      complexity: '高'
    },
    {
      id: 2,
      title: 'Web应用开发',
      description: '现代化的Web应用程序，集成AI功能，提升用户体验',
      icon: <Globe size={40} />,
      features: ['响应式设计', 'AI集成', '数据可视化', '用户管理'],
      price: '¥30,000起',
      duration: '1-4个月',
      complexity: '中'
    },
    {
      id: 3,
      title: '移动应用开发',
      description: '跨平台移动应用，内置智能功能，优化移动端体验',
      icon: <Smartphone size={40} />,
      features: ['跨平台开发', '离线AI', '推送通知', '数据同步'],
      price: '¥40,000起',
      duration: '2-5个月',
      complexity: '中高'
    },
    {
      id: 4,
      title: '数据平台搭建',
      description: '企业级数据处理平台，支持大数据分析和AI模型训练',
      icon: <Database size={40} />,
      features: ['数据管道', '实时分析', '模型训练', '可视化报表'],
      price: '¥80,000起',
      duration: '3-8个月',
      complexity: '高'
    },
    {
      id: 5,
      title: '云端部署',
      description: '云原生架构设计，自动化部署，高可用性保障',
      icon: <Cloud size={40} />,
      features: ['容器化部署', '自动扩缩容', '监控告警', '备份恢复'],
      price: '¥20,000起',
      duration: '1-2个月',
      complexity: '中'
    },
    {
      id: 6,
      title: '系统集成',
      description: '现有系统AI化改造，无缝集成智能功能',
      icon: <Settings size={40} />,
      features: ['API集成', '数据迁移', '功能扩展', '性能优化'],
      price: '¥35,000起',
      duration: '1-3个月',
      complexity: '中高'
    }
  ];

  const process = [
    {
      step: '01',
      title: '需求分析',
      description: '深入了解客户需求，制定详细的项目方案',
      icon: <Users size={32} />
    },
    {
      step: '02',
      title: '方案设计',
      description: '技术架构设计，确定开发路线和时间计划',
      icon: <Code size={32} />
    },
    {
      step: '03',
      title: '开发实施',
      description: '敏捷开发模式，定期交付，持续优化',
      icon: <Zap size={32} />
    },
    {
      step: '04',
      title: '测试部署',
      description: '全面测试验证，安全部署上线',
      icon: <Shield size={32} />
    },
    {
      step: '05',
      title: '维护支持',
      description: '持续维护更新，技术支持保障',
      icon: <Award size={32} />
    }
  ];

  const stats = [
    { number: '200+', label: '完成项目' },
    { number: '98%', label: '客户满意度' },
    { number: '100+', label: '合作企业' },
    { number: '24/7', label: '技术支持' }
  ];

  const caseStudies = [
    {
      id: 1,
      title: '智能客服系统',
      client: '某大型电商平台',
      description: '基于NLP技术的智能客服系统，提升客户服务效率300%',
      technologies: ['自然语言处理', 'React', 'Node.js', '微服务'],
      result: '客户满意度提升40%，人工成本降低60%',
      image: '/uploads/case-chatbot.jpg'
    },
    {
      id: 2,
      title: '智能推荐引擎',
      client: '知名视频平台',
      description: '个性化内容推荐系统，基于用户行为和偏好进行智能推荐',
      technologies: ['机器学习', 'Python', 'TensorFlow', '大数据'],
      result: '用户停留时间增加50%，点击率提升35%',
      image: '/uploads/case-recommendation.jpg'
    },
    {
      id: 3,
      title: '工业质检系统',
      client: '制造业龙头企业',
      description: '基于计算机视觉的自动化质量检测系统',
      technologies: ['计算机视觉', 'PyTorch', '边缘计算', 'IoT'],
      result: '检测精度达到99.5%，效率提升10倍',
      image: '/uploads/case-quality.jpg'
    }
  ];

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>加载中...</p>
      </div>
    );
  }

  return (
    <div className="project-development-page">
      {/* Hero Section */}
      <section className="project-hero">
        <div className="container">
          <div className="hero-content">
            <motion.div
              className="hero-text"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h1>定制项目开发</h1>
              <p className="hero-subtitle">
                量身打造的AI解决方案，满足您的业务需求
              </p>
              <p className="hero-description">
                我们拥有丰富的项目开发经验，专注于为企业提供高质量的AI解决方案。
                从需求分析到部署维护，全程专业服务，助力您的数字化转型。
              </p>
              <div className="hero-buttons">
                <button className="btn-primary">
                  <Play size={20} />
                  开始咨询
                </button>
                <button className="btn-secondary">
                  查看案例
                  <ArrowRight size={20} />
                </button>
              </div>
            </motion.div>

            <motion.div
              className="hero-images"
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              {projectData?.posterImages && projectData.posterImages.length > 0 && (
                <div className="hero-image-grid">
                  {projectData.posterImages.slice(0, 4).map((image, index) => (
                    <motion.img 
                      key={index}
                      src={`http://localhost:5000${image}`} 
                      alt={`项目开发 ${index + 1}`} 
                      className="hero-grid-image"
                      initial={{ opacity: 0, scale: 0.8 }}
                      animate={{ opacity: 1, scale: 1 }}
                      transition={{ duration: 0.6, delay: 0.3 + index * 0.1 }}
                    />
                  ))}
                </div>
              )}
            </motion.div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="project-stats">
        <div className="container">
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                className="stat-item"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="stat-number">{stat.number}</div>
                <div className="stat-label">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section className="project-services">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>我们的服务</h2>
            <p>全方位的AI项目开发服务，满足不同业务需求</p>
          </motion.div>

          <div className="services-grid">
            {services.map((service, index) => (
              <motion.div
                key={service.id}
                className="service-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="service-icon">{service.icon}</div>
                <h3>{service.title}</h3>
                <p className="service-description">{service.description}</p>

                <div className="service-meta">
                  <div className="service-info">
                    <span className="price">{service.price}</span>
                    <span className="duration">{service.duration}</span>
                    <span className={`complexity complexity-${service.complexity}`}>
                      {service.complexity}复杂度
                    </span>
                  </div>
                </div>

                <div className="service-features">
                  {service.features.map((feature, idx) => (
                    <span key={idx} className="feature-tag">
                      <CheckCircle size={14} />
                      {feature}
                    </span>
                  ))}
                </div>

                <button className="btn-service">
                  了解详情
                  <ArrowRight size={16} />
                </button>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Process Section */}
      <section className="project-process">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>开发流程</h2>
            <p>标准化的开发流程，确保项目质量和交付时间</p>
          </motion.div>

          <div className="process-timeline">
            {process.map((step, index) => (
              <motion.div
                key={index}
                className="process-step"
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                <div className="step-number">{step.step}</div>
                <div className="step-content">
                  <div className="step-icon">{step.icon}</div>
                  <h3>{step.title}</h3>
                  <p>{step.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Case Studies Section */}
      <section className="project-cases">
        <div className="container">
          <motion.div
            className="section-header"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>成功案例</h2>
            <p>真实项目案例，展示我们的专业能力</p>
          </motion.div>

          <div className="cases-grid">
            {caseStudies.map((caseStudy, index) => (
              <motion.div
                key={caseStudy.id}
                className="case-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <div className="case-image">
                  <img src={caseStudy.image} alt={caseStudy.title} />
                </div>

                <div className="case-content">
                  <h3>{caseStudy.title}</h3>
                  <p className="case-client">{caseStudy.client}</p>
                  <p className="case-description">{caseStudy.description}</p>

                  <div className="case-technologies">
                    {caseStudy.technologies.map((tech, idx) => (
                      <span key={idx} className="tech-tag">{tech}</span>
                    ))}
                  </div>

                  <div className="case-result">
                    <strong>项目成果：</strong>
                    <p>{caseStudy.result}</p>
                  </div>

                  <button className="btn-case">
                    查看详情
                    <ArrowRight size={16} />
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="project-cta">
        <div className="container">
          <motion.div
            className="cta-content"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <h2>开始您的AI项目</h2>
            <p>让我们一起打造属于您的智能解决方案</p>
            <div className="cta-buttons">
              <button className="btn-primary">
                <Code size={20} />
                免费咨询
              </button>
              <button className="btn-secondary">
                获取报价
                <ArrowRight size={20} />
              </button>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default ProjectDevelopment;
