import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { X } from 'lucide-react';
import axios from 'axios';

const ServiceDetail = ({ serviceId, onClose }) => {
  const [service, setService] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchServiceDetail = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/services/${serviceId}`);
        setService(response.data);
      } catch (err) {
        setError('获取服务详情失败');
        console.error('Error fetching service detail:', err);
      } finally {
        setLoading(false);
      }
    };

    if (serviceId) {
      fetchServiceDetail();
    }
  }, [serviceId]);

  if (loading) {
    return (
      <motion.div 
        className="service-detail-overlay"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <div className="service-detail-modal">
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>加载中...</p>
          </div>
        </div>
      </motion.div>
    );
  }

  if (error) {
    return (
      <motion.div 
        className="service-detail-overlay"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <div className="service-detail-modal">
          <div className="error-message">
            <p>{error}</p>
            <button onClick={onClose} className="btn-primary">关闭</button>
          </div>
        </div>
      </motion.div>
    );
  }

  if (!service) {
    return null;
  }

  return (
    <motion.div 
      className="service-detail-overlay"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      onClick={onClose}
    >
      <motion.div 
        className="service-detail-modal"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        transition={{ type: "spring", damping: 25, stiffness: 300 }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* 头部 */}
        <div className="service-detail-header">
          <button 
            className="close-btn"
            onClick={onClose}
            aria-label="关闭"
          >
            <X size={24} />
          </button>
          <div className="service-detail-title">
            <h2>{service.title}</h2>
            <span className={`service-status ${service.isActive ? 'active' : 'inactive'}`}>
              {service.isActive ? '正在提供' : '暂停服务'}
            </span>
          </div>
        </div>

        {/* 内容 */}
        <div className="service-detail-content">
          {/* 服务描述 */}
          <div className="service-description-section">
            <h3>服务介绍</h3>
            <p>{service.description}</p>
          </div>

          {/* 服务特性 */}
          {service.features && service.features.length > 0 && (
            <div className="service-features-section">
              <h3>服务特性</h3>
              <div className="features-grid">
                {service.features.map((feature, index) => (
                  <motion.div 
                    key={index}
                    className="feature-item"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <CheckCircle size={20} className="feature-icon" />
                    <span>{feature}</span>
                  </motion.div>
                ))}
              </div>
            </div>
          )}

          {/* 服务优势 */}
          <div className="service-advantages-section">
            <h3>服务优势</h3>
            <div className="advantages-grid">
              <div className="advantage-item">
                <Star className="advantage-icon" size={24} />
                <h4>专业团队</h4>
                <p>拥有丰富经验的AI专家团队</p>
              </div>
              <div className="advantage-item">
                <Users className="advantage-icon" size={24} />
                <h4>个性化服务</h4>
                <p>根据客户需求定制专属解决方案</p>
              </div>
              <div className="advantage-item">
                <Calendar className="advantage-icon" size={24} />
                <h4>及时响应</h4>
                <p>7x24小时技术支持和服务保障</p>
              </div>
            </div>
          </div>

          {/* 联系方式 */}
          <div className="service-contact-section">
            <h3>联系我们</h3>
            <p>如果您对此服务感兴趣，请联系我们获取更多信息和报价。</p>
            <div className="contact-buttons">
              <button className="btn-primary">
                立即咨询
              </button>
              <button className="btn-secondary">
                获取报价
              </button>
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default ServiceDetail;
